export declare const LightningReceiveFeeEstimate = "\n  query LightningReceiveFeeEstimate(\n    $network: BitcoinNetwork!\n    $amount_sats: Long!\n  ) {\n    lightning_receive_fee_estimate(\n      input: {\n        network: $network\n        amount_sats: $amount_sats\n      }\n    ) {\n      ...LightningReceiveFeeEstimateOutputFragment\n    }\n  }\n  \nfragment LightningReceiveFeeEstimateOutputFragment on LightningReceiveFeeEstimateOutput {\n    __typename\n    lightning_receive_fee_estimate_output_fee_estimate: fee_estimate {\n        __typename\n        currency_amount_original_value: original_value\n        currency_amount_original_unit: original_unit\n        currency_amount_preferred_currency_unit: preferred_currency_unit\n        currency_amount_preferred_currency_value_rounded: preferred_currency_value_rounded\n        currency_amount_preferred_currency_value_approx: preferred_currency_value_approx\n    }\n}\n";
